import React from 'react'
import "./Resume.scss"
import parse from 'html-react-parser'
import resumeData from "../assets/resume.json"

const Resume = () => {
  const parseHtmlContent = (node) => {
    if (node.attribs && node.attribs.id === "profile-info") {
      // console.log(node);
      return (
        <div className='info'>
          <h1>{node.children[0].children[0].data}</h1>
        </div>
      )
    } else if (node.attribs && node.attribs.id === "social") {
      console.log(node);
      return (
        <div className='info'>
          <h1>{node.children[0].children[0].data}</h1>
        </div>
      )
    } 
  }

  return (
    <div className='resume'>
      { parse(resumeData.content, { replace: parseHtmlContent }) }
    </div>
  )
}

export default Resume
