  return (
    <div className="App">
      {/* {parse(resumeData.content), {
        replace: domNode => {
          if (domNode) {
            return {domNode.children}
          }
        }
      }} */}
      {/* {parse(resumeData, (
            replace: domNode =>(
                // if (domNode.attribs) {
                    return (domNode);
                // }  
            ))
        )} */}
      {parse(resumeData.content, {
        replace: function (domNode) {
          // console.log(domNode.attribs);
          // console.log('children', domNode.children[0].children[0].data);
          console.log(domNode.children);
          if (domNode.attribs && domNode.attribs.id === "profile-info") {
            const reactEle = React.createElement("div", {}, domNode.children[0].parent)
            return reactEle;
          }
          if (domNode.attribs && domNode.attribs.id === "name") {
            const reactEle = React.createElement("h1", {}, domNode.children[0].data)
            return reactEle;
          }
        },
      })}
    </div>
  );
}

export default App;
