            replace: domNode =>(
                // if (domNode.attribs) {
                    return (domNode);
                // }  
            ))
        )} */}
      {parse(resumeData.content, {
        replace: function (domNode) {
          if (domNode.attribs && domNode.attribs.id === "profile-info") {
            return         <span style={{ color: 'hotpink' }}>
            {domToReact(Children, parserOptions)}
          </span>
          }
        },
      })}
    </div>
  );
}

export default App;
